import { Input } from "../../components/Input"
import { useForm } from 'react-hook-form';
import * as yup from 'yup'

export const Login = () => {



    const schema = yup.object().shape({
        email: yup.string().email('E-mail inválido').required('E-mail obrigatória'),
        password: yup.string().required('Senha obrigatória'),
      });
      const {
        register,
        handleSubmit,
        formState: { errors },
      } = useForm({
        resolver: yupResolver(schema),
      });

    const onSubmit = (data) => {
      console.log(data)
    }

    return(
        <div>
        <h1>Login</h1>
        <form onClick={handleSubmit(onSubmit)}>
        <Input name="name" label="Email" register={register} error={errors.email?.message}/>
        <Input name="name" label="Senha" register={register} error={errors.email?.message}/>
        </form>

        </div>

    )
}
